package kr.co.ezenac.operator;

public class PlusMinusSignTest {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		short num1 = 5;
		System.out.println(+num1);
		System.out.println(-num1);		// 부호를 바궈써 얻은 결과를 출력했다
		System.out.println(num1);		// num1 값은 변하지 않는다
		
	}

}
